parameters.text:
=You will be given text that contains many separate prompts / scripts for a UGC short form video. I want you to extract these into an array of strings where each item extracted is the FULL prompt/script. The prompts should remain exactly as they were provided.

---
{{ $json.choices[0].message.content }}

parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Extracted Prompts",
  "description": "A schema designed to hold a list of individual prompts that have been extracted from a larger body of text. Use this to format the extracted prompts.",
  "type": "object",
  "properties": {
    "prompts": {
      "type": "array",
      "description": "An array where each element is a single, distinct prompt string that was extracted from the user's input.",
      "items": {
        "type": "string",
        "description": "A single prompt statement."
      }
    }
  },
  "required": [
    "prompts"
  ]
}

parameters.responseFormat:
{
  "type": "json_schema",
    "schema": {
      "type": "object",
      "properties": {
        "object_id": {
          "type": "string",
          "pattern": "^[a-z]+_[0-9a-f]{6}$",
          "description": "The artifact object ID only (e.g., img_5ae97f, url_94e682, vid_123456)"
        }
      },
      "required": ["object_id"],
      "additionalProperties": false
    },
    "strict": true
}

